import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import axios from 'axios';
import { addTask } from '../redux/tasksSlice';
import { useNavigate } from 'react-router-dom';

const AddTask = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    const newTask = {
      title,
      description,
      completed: false,
    };

    try {
      const response = await axios.post('https://jsonplaceholder.typicode.com/todos', newTask);
      dispatch(addTask(response.data));
      navigate('/');
    } catch (error) {
      console.error('Error adding task', error);
    }
  };

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-2xl font-semibold mb-4">Add New Task</h1>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label htmlFor="title" className="block">Title</label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className="w-full px-4 py-2 border rounded"
            required
          />
        </div>
        <div>
          <label htmlFor="description" className="block">Description</label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            className="w-full px-4 py-2 border rounded"
          />
        </div>
        <button type="submit" className="btn btn-primary w-full">Add Task</button>
      </form>
    </div>
  );
};

export default AddTask;
