import React, { useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';
import axios from 'axios';
import { updateTask } from '../tasksSlice';
import { useParams, useHistory } from 'react-router-dom';

const EditTask = () => {
  const { id } = useParams();
  const dispatch = useDispatch();
  const history = useHistory();
  const [task, setTask] = useState(null);

  useEffect(() => {
    axios
      .get(`https://jsonplaceholder.typicode.com/todos/${id}`)
      .then((response) => {
        setTask(response.data);
      })
      .catch((error) => {
        console.error('Error fetching task', error);
      });
  }, [id]);

  const handleStatusChange = () => {
    const updatedTask = { ...task, completed: !task.completed };
    axios
      .put(`https://jsonplaceholder.typicode.com/todos/${id}`, updatedTask)
      .then((response) => {
        dispatch(updateTask(response.data));
        history.push('/');
      })
      .catch((error) => {
        console.error('Error updating task', error);
      });
  };

  if (!task) return <p>Loading...</p>;

  return (
    <div className="max-w-xl mx-auto p-4">
      <h1 className="text-3xl font-bold text-center mb-8">Edit Task</h1>
      <h3 className="text-xl font-semibold">{task.title}</h3>
      <p className="text-sm text-gray-500">{task.description}</p>
      <div className="mt-6">
        <button
          onClick={handleStatusChange}
          className={`px-6 py-3 w-full font-semibold rounded-md ${
            task.completed ? 'bg-red-500' : 'bg-green-500'
          } text-white hover:bg-opacity-80 transition`}
        >
          {task.completed ? 'Mark as Pending' : 'Mark as Completed'}
        </button>
      </div>
    </div>
  );
};

export default EditTask;
