import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setTasks } from '../tasksSlice';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Home = () => {
  const dispatch = useDispatch();
  const tasks = useSelector((state) => state.tasks.tasks);

  useEffect(() => {
    axios
      .get('https://jsonplaceholder.typicode.com/todos')
      .then((response) => {
        dispatch(setTasks(response.data));
      })
      .catch((error) => {
        console.error('Error fetching tasks', error);
      });
  }, [dispatch]);

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h1 className="text-3xl font-bold text-center mb-8">To-Do List</h1>
      <Link
        to="/add-task"
        className="inline-block mb-4 px-6 py-2 bg-blue-500 text-white font-bold rounded-md hover:bg-blue-600 transition"
      >
        Add Task
      </Link>
      <ul className="space-y-4">
        {tasks.map((task) => (
          <li key={task.id} className="border p-4 rounded-lg shadow-md">
            <h3 className="text-xl font-semibold">{task.title}</h3>
            <p className="text-sm text-gray-500">{task.completed ? 'Completed' : 'Pending'}</p>
            <div className="mt-4 flex space-x-4">
              <Link
                to={`/edit-task/${task.id}`}
                className="px-4 py-2 bg-yellow-500 text-white font-semibold rounded-md hover:bg-yellow-600 transition"
              >
                Edit
              </Link>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Home;
