import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import axios from 'axios';
import { updateTask } from '../redux/tasksSlice';
import { useNavigate, useParams } from 'react-router-dom';

const EditTask = () => {
  const [task, setTask] = useState(null);
  const [completed, setCompleted] = useState(false);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { id } = useParams();

  useEffect(() => {
    const fetchTask = async () => {
      try {
        const response = await axios.get(`https://jsonplaceholder.typicode.com/todos/${id}`);
        setTask(response.data);
        setCompleted(response.data.completed);
      } catch (error) {
        console.error('Error fetching task', error);
      }
    };

    fetchTask();
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const updatedTask = { ...task, completed };
      await axios.put(`https://jsonplaceholder.typicode.com/todos/${id}`, updatedTask);
      dispatch(updateTask(updatedTask));
      navigate('/');
    } catch (error) {
      console.error('Error updating task', error);
    }
  };

  if (!task) return <div>Loading...</div>;

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-2xl font-semibold mb-4">Edit Task</h1>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label htmlFor="completed" className="block">Completed</label>
          <input
            type="checkbox"
            id="completed"
            checked={completed}
            onChange={(e) => setCompleted(e.target.checked)}
            className="w-4 h-4"
          />
        </div>
        <button type="submit" className="btn btn-primary w-full">Update Task</button>
      </form>
    </div>
  );
};

export default EditTask;
