import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setTasks } from '../redux/tasksSlice';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Home = () => {
  const dispatch = useDispatch();
  const tasks = useSelector(state => state.tasks.tasks);

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await axios.get('https://jsonplaceholder.typicode.com/todos');
        dispatch(setTasks(response.data));
      } catch (error) {
        console.error('Failed to fetch tasks', error);
      }
    };

    fetchTasks();
  }, [dispatch]);

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-2xl font-semibold mb-4">To-Do List</h1>
      <Link to="/add-task" className="btn btn-primary mb-4">Add Task</Link>
      <div className="grid grid-cols-1 gap-4">
        {tasks.map((task) => (
          <div key={task.id} className="p-4 border rounded-lg flex justify-between items-center">
            <div>
              <h2 className="font-medium">{task.title}</h2>
              <p>Status: {task.completed ? 'Completed' : 'Pending'}</p>
            </div>
            <div className="flex space-x-2">
              <Link to={`/edit-task/${task.id}`} className="text-blue-500">Edit</Link>
              <button
                onClick={() => dispatch(deleteTask(task.id))}
                className="text-red-500"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
